import axios from 'axios';
import React, { useRef, useState, useEffect, useHistory } from 'react';

const LoginPage = (props) => {

    

    const [cred, setCred] = useState({});
    const enteredEmail = useRef();
    const enteredPassword = useRef();

    useEffect(() => {
        axios.post('http://localhost:3000/login', cred)
            .then(res => {
                console.log('after login res.data := ', res.data);
            })
    }, [cred])

    const submitHandler = (event) => {
        event.preventDefault();
        setCred({
            email: enteredEmail.current.value,
            password: enteredPassword.current.value
        })
    }

    return (
        <div>
            <form onSubmit={submitHandler}>
                <input type="email" name="username" ref={enteredEmail} required />
                <input type="password" name="password" ref={enteredPassword} required />
                <button type="submit">Login</button>
            </form>
        </div>
    )
}


export default LoginPage
