import React, { Fragment, useRef, useState } from 'react'

const RegistrationPage = () => {

    const nameInputRef = useRef();
    const emailInputRef = useRef();
    const usernameInputRef = useRef();
    const passwordInputRef = useRef();
    const [countryInput, setCountryInput] = useState();
    const [accountType,setAccountType] = useState();


    const [page, setPage] = useState(1);

    let indianStates = new Array("Andaman and Nicobar Islands", "Andhra Pradesh", "Arunachal Pradesh", "Assam", "Bihar", "Chandigarh", "Chhattisgarh", "Dadra and Nagar Haveli", "Daman and Diu", "Delhi", "Goa", "Gujarat", "Haryana", "Himachal Pradesh", "Jammu and Kashmir", "Jharkhand", "Karnataka", "Kerala", "Lakshadweep", "Madhya Pradesh", "Maharashtra", "Manipur", "Meghalaya", "Mizoram", "Nagaland", "Orissa", "Pondicherry", "Punjab", "Rajasthan", "Sikkim", "Tamil Nadu", "Tripura", "Uttaranchal", "Uttar Pradesh", "West Bengal");
    let spainStates = new Array("Barcelona", "Madrid", "Sevilla", "Malaga", "Cadiz", "Zaragoza", "Granada", "Valencia", "Cordoba", "Girona", "Almeria", "Toledo");
    const formHandler = () => {
        const eneteredName = nameInputRef.current.value;
        const eneteredEmail = emailInputRef.current.value;
    }

    const checkDate = (event) => {
        console.log(event.target.value);
    }


    return (
        <div className="container mt-5">
            <form onSubmit={formHandler} className="form-group">
                {(page === 1) &&
                    <Fragment>
                        {/**1 */}
                        <input className="form-control" ref={nameInputRef} placeholder="Enter Your Full Name" required />
                        {/**10 */}
                        <input className="form-control"  ref={emailInputRef} placeholder="Enter Your Email Address" required />
                        {/**2 */}
                        <input className="form-control"  ref={usernameInputRef} placeholder="Enter Login Name (username)" required />
                        {/**3 */}
                        <input className="form-control"  ref={passwordInputRef} placeholder="Enter Your Password for Login" minLength="8" required />

                        <div className="row">
                            <div className="col-4">
                                <label>Date of Birth</label>
                                 {/* 14 , date of birth DD/MM/YYYY ,  side age check for minor */}
                                 <input className="form-control"  name="dob" type="date" max={`${new Date()}`} onBlur={checkDate} required />
                            </div>
                            <div className="col-4">
                                {/* gender 11*/}
                                <label for="gender">Gender</label>
                                <select name="gender" id="gender">
                                    <option value="female">Female</option>
                                    <option value="male">Male</option>
                                    <option value="other">Other</option>
                                </select>

                            </div>
                            <div className="col-4">

                            </div>
                        </div>
                       
                      

                        {/*marital status 12 */}
                        <select name="marital-status" id="marital-status">
                            <option value="single">Single</option>
                            <option value="married">Married</option>
                            <option value="divorce">Divorce</option>
                        </select>

                        {/**13 */}
                        <input className="form-control"  name="contact" minLength="10" placeholder="Enter Your Contact Number" id="contact" required />

                    </Fragment>
                }
                {
                    (page === 2) &&
                    <Fragment>

                        {/* guardian type 4 */}
                        <select name="guardian-type" id="guardian-type">
                            <option value="local">Option 1</option>
                            <option value="parental">Option 2</option>
                        </select>
                        {/**5 */}
                        <input type="text" className="form-control"  placeholder="Enter Your Guardian Name" name="guardian-name" required />

                        {/**6 */}
                        <input type="text" className="form-control"  placeholder="Enter Your Full Address with PinCode" name="address" required />
                        {/**7 */}
                        <input type="text" className="form-control"  placeholder="Enter Your Citizenship" name="citizenship" required />
                        {/**9 */}
                        <select name="country-name" required onChange={(e) => { setCountryInput(e.target.value) }}>
                            <option value="india">India</option>
                            <option value="spain">Spain</option>
                        </select>
                        {/**8 */}
                        {/**state dropdown based on country */}
                        <select name="states" id="states">

                            {
                                (countryInput === 'india') &&
                                <Fragment>
                                    {indianStates.map((state, index) => <option value={index}>{state}</option>)}
                                </Fragment>
                            }
                            {
                                (countryInput === 'spain') &&
                                <Fragment>
                                    {spainStates.map((state, index) => <option value={index}>{state}</option>)}
                                </Fragment>
                            }
                        </select>

                        {/**18  */}
                        <input type="text" className="form-control"  placeholder="Enter Your Citizen Status" 
                            name="citizenship-status" required />
                    </Fragment>
                }

                {
                    (page===3) &&
                    <Fragment>
                        {/* 15 Registration Date- Represents the customer registration Date 
                         which is automatically populated from the system current date  */}
                        <input className="form-control"  type="date" value={new Date()} onBlur={checkDate} 
                            name="registration-date" required />

                        {/** 16 , account type options=salary/savings*/}
                        <select name="account-type" id="account-type" onChange={(e)=>{
                            setAccountType(e.target.value);
                        }}>
                            <option value="savings">Savings</option>
                            <option value="salary">Salary</option>
                        </select>

                        {/**17 ,  */}
                        <input className="form-control" type="text"  placeholder="Enter Your Branch Name" required />

                        {/**19 */}
                        <input className="form-control"  type="number" placeholder="Enter Your Initial Deposit Amount" value={()=>{
                            return (accountType==='savings')?5000:0;
                        }} required />
                        {/**set it according to account type */}

                        {/**20 */}
                        <input className="form-control"  placeholder="Enter Your Identification Proof Type" name="proof-type" required />
                        {/**21 */}
                        <input className="form-control"  type="text" placeholder="Identification Document No."></input>
                        <input className="form-control"  type="text" placeholder="Enter Reference account holder name" name="reference-acc-holder-name" required />
                        <input className="form-control"  type="number" placeholder="Enter Reference account holder acc. No." name="reference-acc-no." required />
                        <input className="form-control"  type="text" placeholder="Enter Reference account holder address" name="reference-acc-address" required />
                    </Fragment>
                }
            </form>
            <div className="row">
                <div className="col-2"><button onClick={()=>setPage(page-1)}>Prev</button></div>
                <div className="col-8"></div>
                <div className="col-2"><button onClick={()=>setPage(page+1)}>Next</button></div>
            </div>

        </div>
    )
}

export default RegistrationPage;
