import React, { Fragment, useState } from 'react'

const RegistrationPage = () => {

    const [nameInput,setNameInput] = useState();
    const [emailInput,setEmailInput] = useState();
    const [usernameInput,setUsernameInput] = useState();
    const [passwordInput,setPasswordInput] = useState();
    const [countryInput,setCountryInput] = useState();
    const [accountType,setAccountType] = useState();
    const [gender,setGender] = useState();
    const [dob,setDob] = useState();
    const [maritalStatus,setMaritalStatus] = useState();
    const [contactInput,setContactInput] = useState(0)
    const [guardianType,setGuardianType] = useState();

    let indianStates = ["Andaman and Nicobar Islands", "Andhra Pradesh", "Arunachal Pradesh", "Assam", "Bihar", "Chandigarh", "Chhattisgarh", "Dadra and Nagar Haveli", "Daman and Diu", "Delhi", "Goa", "Gujarat", "Haryana", "Himachal Pradesh", "Jammu and Kashmir", "Jharkhand", "Karnataka", "Kerala", "Lakshadweep", "Madhya Pradesh", "Maharashtra", "Manipur", "Meghalaya", "Mizoram", "Nagaland", "Orissa", "Pondicherry", "Punjab", "Rajasthan", "Sikkim", "Tamil Nadu", "Tripura", "Uttaranchal", "Uttar Pradesh", "West Bengal"];
    let spainStates = ["Barcelona", "Madrid", "Sevilla", "Malaga", "Cadiz", "Zaragoza", "Granada", "Valencia", "Cordoba", "Girona", "Almeria", "Toledo"];
    const formHandler = (event) => {
       event.preventDefault();
    }

    const checkDate = (event) => {
        console.log(event.target.value);
    }

    let currentDate = new Date();

    return (
        <div className="container mt-5">
            <form onSubmit={formHandler} className="form-group">
                {
                    <Fragment>
                        {/**1 */}
                        <input className="form-control" defaultValue={nameInput}  placeholder="Enter Your Full Name" required />
                        {/**10 */}
                        <input className="form-control" defaultValue={emailInput}  placeholder="Enter Your Email Address" required />
                        {/**2 */}
                        <input className="form-control" defaultValue={usernameInput} placeholder="Enter Login Name (username)" required />
                        {/**3 */}
                        <input className="form-control" defaultValue={passwordInput} placeholder="Enter Your Password for Login" minLength="8" required />

                        <div className="row">
                            <div className="col-md-4">
                                <label for="dob">Date of Birth</label>{/* 14 , date of birth DD/MM/YYYY ,  side age check for minor */}
                                <input className="form-control" name="dob" defaultValue={dob} type="date" max={currentDate} onBlur={checkDate} required />
                            </div>
                            <div className="col-md-4">
                                <label for="gender">Gender</label>{/* gender 11*/}
                                <select name="gender" id="gender" onChange={(e)=>setGender(e.target.value)}>
                                    <option value="female" selected={(gender==='female')}>Female</option>
                                    <option value="male" selected={(gender==='male')}>Male</option>
                                    <option value="other" selected={(gender==='other')}>Other</option>
                                </select>

                            </div>
                            <div className="col-md-4">
                                <label for="marital-status">Marital Status</label>{/*marital status 12 */}
                                <select name="marital-status" id="marital-status" onChange={(e)=>setMaritalStatus(e.target.value)}>
                                    <option value="single" selected={(maritalStatus==='single')}>Single</option>
                                    <option value="married" selected={(maritalStatus==='married')}>Married</option>
                                    <option value="divorce" selected={(maritalStatus==='divorce')}>Divorce</option>
                                </select>
                            </div>
                        </div>

                        {/**13 */}
                        <input type="number" className="form-control" defaultValue={contactInput}
                         name="contact" minLength="10" maxLength="10" placeholder="Enter Your Contact Number" id="contact" required />

                    </Fragment>
                }
                {

                    <Fragment>
                        <label for="guardian-type">Guardian Type</label>{/* guardian type 4 */}
                        <select name="guardian-type" id="guardian-type">
                            <option value="local">Option 1</option>
                            <option value="parental">Option 2</option>
                        </select>

                        {/**5 */}
                        <input type="text" className="form-control" placeholder="Enter Your Guardian Name" name="guardian-name" required />

                        {/**6 */}
                        <input type="text" className="form-control" placeholder="Enter Your Full Address with PinCode" name="address" required />
                        {/**7 */}
                        <input type="text" className="form-control" placeholder="Enter Your Citizenship" name="citizenship" required />
                        <div className="row">
                            <label for="country-name">Country</label>
                            {/**9 */}
                            <select name="country-name" required ref={countryInputRef}>
                                <option value="india">India</option>
                                <option value="spain">Spain</option>
                            </select>
                        </div>

                        <div className="row">
                            <label for="states">State</label>
                            {/**8 */}
                            {/**state dropdown based on country */}
                            <select name="states" id="states">

                                {
                                    (countryInputRef.current.value === 'india') &&
                                    <Fragment>
                                        {indianStates.map((state, index) => <option value={index}>{state}</option>)}
                                    </Fragment>
                                }
                                {
                                    (countryInputRef.current.value === 'spain') &&
                                    <Fragment>
                                        {spainStates.map((state, index) => <option value={index}>{state}</option>)}
                                    </Fragment>
                                }
                            </select>
                        </div>



                        {/**18  */}
                        <input type="text" className="form-control" placeholder="Enter Your Citizen Status"
                            name="citizenship-status" required />
                    </Fragment>
                }

                {

                    <Fragment>
                        <div className="row">
                            <div className="col-md-6">
                                <label for="registration-date">Registration Date</label>
                                {/* 15 Registration Date- Represents the customer registration Date 
                         which is automatically populated from the system current date  */}
                                <input className="form-control" type="date" value={currentDate} onBlur={checkDate}
                                    name="registration-date" required />

                            </div>
                            <div className="col-md-6">
                                <label for="account-type">Account Type</label><br></br>
                                {/** 16 , account type options=salary/savings*/}
                                <select name="account-type" id="account-type" ref={accountTypeRef}>
                                    <option value="savings">Savings</option>
                                    <option value="salary">Salary</option>
                                </select>
                            </div>
                        </div>




                        {/**17 ,  */}
                        <input className="form-control" type="text" placeholder="Enter Your Branch Name" required />

                        {/**19 */}
                        <input className="form-control" type="number" placeholder="Enter Your Initial Deposit Amount" value={() => {
                            return (accountTypeRef.current.value === 'savings') ? 5000 : 0;
                        }} required />
                        {/**set it according to account type */}

                        {/**20 */}
                        <input className="form-control" placeholder="Enter Your Identification Proof Type" name="proof-type" required />
                        {/**21 */}
                        <input className="form-control" type="text" placeholder="Identification Document No."></input>
                        <input className="form-control" type="text" placeholder="Enter Reference account holder name" name="reference-acc-holder-name" required />
                        <input className="form-control" type="number" placeholder="Enter Reference account holder acc. No." name="reference-acc-no." required />
                        <input className="form-control" type="text" placeholder="Enter Reference account holder address" name="reference-acc-address" required />
                    </Fragment>
                }
                <div className="row">
                    <div className="col-12"><button className="btn btn-success"> Submit</button></div>
                </div>
            </form>


        </div>
    )
}

export default RegistrationPage;
