import React, { useState } from 'react'

const ApplyLoanPage = () => {

    const [loanType, setLoanType] = useState("educational");
    const [loanAmount, setLoanAmount] = useState();
    const [loanApplyDate, setLoanApplyDate] = useState();
    const [issueDate,setIssueDate] = useState()
    const [rateOfInterest, setRateOfInterest] = useState();
    const [durationInput, setDurationInput] = useState();

    

    return (
        <div className="container mt-5">
            <form className="from-group">
                <label for="loan-type">Loan Type</label>
                <select name="loan-type" id="loan-type" onChange={(e) => setLoanType(e.target.value)}>
                    <option value='educational' selected={loanType === 'educational'}>Eductional</option>
                    <option value='personal' selected={loanType === 'personal'}>Personal</option>
                </select>

                <label htmlFor="loanAmount">Loan Amount</label>
                <input
                    className="form-control"
                    defaultValue={loanAmount}
                    onChange={(e)=>{setLoanAmount(e.target.value)}}
                    type="number"
                    name="loanAmount"
                    placeholder="Loan Amount" required />

                <label htmlFor="applyDate">Apply Date</label>
                <input
                    className="form-control"
                    type="date"
                    defaultValue={loanApplyDate}
                    onChange={(e)=>setLoanApplyDate(e.target.value)}
                    name="applyDate"
                    id="applyDate" />

                <label htmlFor="issueDate">Issue Date</label>
                <input
                    className="form-control"
                    type="date"
                    defaultValue={issueDate}
                    onChange={(e)=>setIssueDate(e.target.value)}
                    name="issueDate"
                    id="issueDate" />

                <label htmlFor="rateOfInterest">Rate of Interest</label>
                <input
                    className="form-control"
                    type="number"
                    defaultValue={rateOfInterest}
                    onChange={(e)=>setRateOfInterest(e.target.value)}
                    name="rateOfInterest"
                    placeholder="Rate of Interest" />

                <label htmlFor="duration">Duration of Loan</label>
                <select name="duration" id="duration" onChange={(e) => { setDurationInput(e.target.value); }}>
                    <option value="5" selected={durationInput === '5'}>5</option>
                    <option value="10" selected={durationInput === '10'}>10</option>
                    <option value="15" selected={durationInput === '15'}>15</option>
                    <option value="20" selected={durationInput === '20'}>20</option>

                </select>
                {
                    (loanType === "educational") && (
                       <EducationalLoan/>
                    )
                }
                {
                    (loanType == "personal") && (
                        <div>
                            <input className="form-control" type="number" placeholder="Annual Income" required />

                            <input className="form-control" placeholder="Current Organisation" required />

                            <input className="form-control" placeholder="Designation in Current Organisation" required />

                            <input className="form-control" placeholder="Father's Total Experience" required />

                            <input className="form-control" placeholder="Experience in Current Organisation" required />

                        </div>
                    )
                }
            </form>
        </div>
    )
}

export default ApplyLoanPage
