import {GET_USER_BY_ID,GET_ALL_USER,EDIT_USER,ADD_USER, LOGIN_USER} from '../actions/userActions';

let user={}

let initialState = {
    users:[],
    loggedInUser:user,
    token:null
}

const userReducer = (state=initialState,action)=>{
    let newState;
    console.log(action.type);
    switch(action.type)
    {
        
        case ADD_USER:
             newState = {
                 ...state,
                users:[...state.users,action.payload],
                loggedInUser:state.loggedInUser
            }
            return newState;
        case GET_ALL_USER:
             newState = {
                 ...state,
                users:action.payload,
            }
            return newState;

        case GET_USER_BY_ID:
             newState = {
                ...state,
                loggedInUser:action.payload
            }
            return newState;

        case EDIT_USER:
             newState={
                ...state,
                users:[state.users.slice(0,action.index),action.payload,state.users.slice(action.index+1)],
            }
            return newState;
        case LOGIN_USER :
            console.log(action.token);
            newState={
                ...state,
                token:action.token,
                loggedInUser:action.loggedInUser
            }
            return newState;

        default: return state;
    }
}

export default userReducer;
